import React, { useState } from "react";
import { links } from "../../assets/images-links";
import "./styles.css";
import NavigateNextRoundedIcon from '@mui/icons-material/NavigateNextRounded';

function Filter() {

  const [selectedFilter, setSelectedFilter] = useState(null);

  return (
    <div className="filter-div">
      {links.map((item, i) => (
        <div
          key={i}
          className={`links-box ${i === selectedFilter ? "selected-box" : ""}`}
          onClick={() => {
            console.log("selecting key", i);
            setSelectedFilter(i);
          }}
        >
          <img src={item.imgSrc} className="links-img" alt="" />
          <p className={`links-label ${i === selectedFilter ? "selected-label" : ""}`}>
            {item.label}
          </p>
        </div>
      ))}
      <div className="navigate-next">
        <NavigateNextRoundedIcon />
      </div>
    </div>
  );
}

export default Filter;
